import React, { useState } from 'react';

const App = () => {

const [inputlist,setinputlist] = useState();
const [Items,setItems] = useState([]);

const itemEvent = (e) => {
  setinputlist(e.target.value);
};

const listofItems = () =>{
  setItems((olditems) =>{
    return [...olditems, inputlist]
  })
};

return(
  <div className='masla'>
  <div className='dabba'>
    <h2>
      Behal's ToDo List
    </h2>
    <input type='text' placeholder='Mention Task' onChange={itemEvent}/>
    <button onClick={listofItems}> + </button>
  </div>
  <div className='list'>
            <h2>Your Tasks</h2>
                <ul>
                 {Items.map((itemval) => {
                      return 
                      (<>
                        <i class='fa fa-remove red-color'></i>
                      <li>{itemval}</li></>
                      );
                    })}:
                </ul>
            </div>
  </div>
)
};

export default App;
