import React, { useState } from 'react';

const App=()=>{
const [inputlist,setinputlist] = useState();
const itemEvent = (e)=>{
    setinputlist(e.target.value);
}

    return(
            <div className='masla'>
                <div className='dabba'>
                <h2>Behal's ToDo List</h2>
                <input type='text' onChange={itemEvent} placeholder='Mention Task'/>
                <button on> + </button>
                </div>
                <div className='task'>
                    <h2>Your Tasks</h2>
                    <ul>
                        <li>{inputlist}</li>
                    </ul>
                </div>
            </div>
    )
};
export default App;
