import React, { useState } from 'react';

const App=()=>{
const [inputlist,setinputlist] = useState();
const [items,setitems]= useState([]);

const itemEvent = (e)=>{
    setinputlist(e.target.value);
}
const listofitems=()=>{
    setitems((olditems)=>{
        return []
    })
};

    return(
            <div className='masla'>
                <div className='dabba'>
                <h2>Behal's ToDo List</h2>
                <input type='text' onChange={itemEvent} placeholder='Mention Task'/>
                <button onClick={listofitems}> + </button>
                </div>
                <div className='task'>
                    <h2>Your Tasks</h2>
                    <ul>
                        <li>{inputlist}</li>
                        {items.map((itemval)=>{
                            return <li>{setitems}</li>
                        })}
                    </ul>
                </div>
            </div>
    )
};
export default App;
